BEGIN;

DROP TABLE IF EXISTS "tree", "command", "user", "command_has_tree";

CREATE TABLE "tree" (
  "id" INT PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
  "name" VARCHAR(32) NOT NULL UNIQUE,
  "slug" VARCHAR(32) NOT NULL UNIQUE,
  "image" VARCHAR(255) NOT NULL,
  "variety" VARCHAR(32) NOT NULL,
  "size" DECIMAL NOT NULL,
  "price_ht" DECIMAL NOT NULL,
  "price_ttc" DECIMAL NOT NULL,
  "origin" VARCHAR(64) NOT NULL,
  "created_at" TIMESTAMPTZ NOT NULL DEFAULT NOW(),
  "updated_at" TIMESTAMPTZ
);

CREATE TABLE "user" (
  "id" INT PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
  "role" VARCHAR(32) NOT NULL,
  "lastname" VARCHAR(32) NOT NULL,
  "firstname" VARCHAR(32) NOT NULL,
  "adress" VARCHAR(255) NOT NULL,
  "email" VARCHAR(255) NOT NULL,
  "password" VARCHAR(32) NOT NULL,
  "created_at" TIMESTAMPTZ NOT NULL DEFAULT NOW(),
  "updated_at" TIMESTAMPTZ
);

CREATE TABLE "command" (
  "id" INT PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
  "date" TIMESTAMPTZ NOT NULL,
  "status" VARCHAR(255) NOT NULL,
  "total_price" DECIMAL NOT NULL,
  "user_id" INT NOT NULL REFERENCES "user"("id"),
  "created_at" TIMESTAMPTZ NOT NULL DEFAULT NOW(),
  "updated_at" TIMESTAMPTZ
);

CREATE TABLE "command_has_tree" (
  "id" INT PRIMARY KEY GENERATED BY DEFAULT AS IDENTITY,
  "tree_id" INT NOT NULL REFERENCES "tree"("id"),
  "command_id" INT NOT NULL REFERENCES "command"("id"),
  "quantity" INT NOT NULL,
  "created_at" TIMESTAMPTZ NOT NULL DEFAULT NOW(),
  "updated_at" TIMESTAMPTZ
);

COMMIT;